import { Directive ,ElementRef,HostBinding,HostListener, ViewChild} from '@angular/core';

@Directive({
  selector: '[appStyle]'
})
export class StyleDirective {

  @ViewChild("sign")marker!:ElementRef;
  constructor() { 

  }



  @HostBinding('class') classname='light';




  @HostListener('click') onClick(){


    this.marker.nativeElement.style.color="yellow";
    if(this.classname=="light"){
      this.classname="dark";

    }else{
      this.classname="light";

    }

  }
}
