import { Directive ,HostBinding,HostListener} from '@angular/core';

@Directive({
  selector: '[appStyle]'
})
export class StyleDirective {

  constructor() { }


  @HostBinding('style.color') color:string="black";
  @HostBinding('style.background') bg="black"


  @HostListener('click') onClick(){

    if(this.color=="black"){
      this.color="white";
    }else{
      this.color="black";
    }

  }
}
