import { Component, HostBinding, HostListener,ViewChild,ElementRef, AfterViewInit } from '@angular/core';

@Component({
  selector: 'app-host',
  templateUrl: './host.component.html',
  styleUrls: ['./host.component.css']
})
export class HostComponent implements AfterViewInit {

  // @HostBinding('style.color') color:string='red';

  @ViewChild("sign")marker!:ElementRef;
  constructor(){



  }
  ngAfterViewInit(): void {

    this.marker.nativeElement.style.color="orange";

  }





  @HostListener('click') onClick(){




    if(this.mark=="red"){
      this.color="blue";

    }else{
      this.color="red";

    }

  }
}
